mapper.insert:
INSERT INTO tweet (
	id
	, user
	, user_no
	, tweet_text
	, url
	, tweet_type
	, target_user
	, reply_user
	, reply_user_no
	, retweeted_user
	, mentioned_user
	, mentioned_users
	, positive_attitude
	, negative_attitude
	, create_date
	, col_create_date
	, col_update_date
)
VALUES (
	#{id,javaType=long,jdbcType=NUMERIC}
	, #{user}
	, #{userNo,javaType=long,jdbcType=NUMERIC}
	, #{text}
	, #{url}
	, #{tweetType}
	, #{targetUser}
	, #{replyUser}
	, #{replyUserNo,javaType=long,jdbcType=NUMERIC}
	, #{retweetedUser}
	, #{mentionedUser}
	, #{mentionedUsers}
	, #{positiveAttitude,javaType=boolean,jdbcType=BOOLEAN}
	, #{negativeAttitude,javaType=boolean,jdbcType=BOOLEAN}
	, #{createDate,jdbcType=DATE}
	, NOW()
	, NOW()
)

mapper.update:
UPDATE tweet
positive_attitude = #{positiveAttitude}, negative_attitude = #{negativeAttitude}, col_update_date = NOW()
WHERE id = #{id}
