mapper.insert:
INSERT INTO user (
	id
	, user
	, name
	, location
	, profile_image_url
	, followers_count
	, friends_count
	, tweets_count
	, is_target
	, col_create_date
	, col_update_date
)
VALUES (
	#{id}
	, #{user}
	, #{name}
	, #{location}
	, #{profileImageUrl}
	, #{followersCount,javaType=int,jdbcType=NUMERIC}
	, #{friendsCount,javaType=int,jdbcType=NUMERIC}
	, #{tweetsCount,javaType=int,jdbcType=NUMERIC}
	, #{isTarget,javaType=boolean,jdbcType=BOOLEAN}
	, #{colCreateDate,jdbcType=DATE}
	, #{colCreateDate,jdbcType=DATE}
	, NOW()
	, NOW()
)
