mapper.select:
SELECT *
FROM term
WHERE 1 = 1

	AND pogram_id = #{programId}


	AND start_date = #{startDate}


	AND end_date = #{endDate}


	AND type = #{type}

ORDER BY tf DESC

mapper.insert:
INSERT INTO term (
	program_id
	, start_date
	, end_date
	, type
	, term
	, duration_type
	, feature
	, tf
	, idf
	, score
	, register_date
	, update_date		
)
VALUES (
	#{programId}
	, #{startDate}
	, #{endDate}
	, #{type}
	, #{term}
	, #{durationType}
	, #{feature}
	, #{tf}
	, #{idf}
	, #{score}
	, NOW()
	, NOW()
)

mapper.delete:
DELETE FROM term
WHERE program_id = #{programId}

	AND start_date = #{startDate}


	AND end_date = #{endDate}


	AND type = #{type}
